FloatPanel.ClientEvent:
var data = this.get("#viewSelectCustomer.userData");
if (data != null) {
	var entity = this.get("#dataSetTransportTemplate").getData("#");
	var templateType=entity.get("templateType");
	var value="";
	if(templateType=="S"){
		value="标准模板";
	}else{
		value="常规模板";
	}
    entity.set({
        customerId:data.get("customerid"),
		customerName:data.get("customername"),
		templateName:data.get("customername")+'-'+value
    });
}

var data = this.get("#viewSelectCustomer1.userData");
if (data != null) {
	var entity = this.get("#dataSetCondition").getData("#");
    entity.set({
        customerId:data.get("customerid"),
    });
}
